package org.sidequest.parley.scripts;

import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.SerializationFeature;
import org.sidequest.parley.scripts.analyzers.CodeAnalyzer;
import org.sidequest.parley.scripts.analyzers.DatabaseAnalyzer;
import org.sidequest.parley.scripts.model.ProjectStructureModels.*;

import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.*;
import java.util.stream.Collectors;

public class AIStructureGenerator {
    private static final ObjectMapper mapper = new ObjectMapper()
            .enable(SerializationFeature.INDENT_OUTPUT);

    private static final List<String> IGNORE_DIRECTORIES = Arrays.asList(
            ".git", ".idea", "node_modules", "target", "bin", "build", "dist");

    public static void main(String[] args) {
        try {
            String projectRoot = findProjectRoot();
            if (projectRoot == null) {
                System.out.println("Could not find project root directory.");
                return;
            }

            ProjectStructure structure = analyzeProject(new File(projectRoot));
            String json = mapper.writeValueAsString(structure);

            // Write to file
            Path outputPath = Paths.get(projectRoot, "ai-project-structure.json");
            Files.write(outputPath, json.getBytes());
            System.out.println("AI-optimized project structure written to: " + outputPath);
        } catch (Exception e) {
            System.err.println("Error generating project structure: " + e.getMessage());
            e.printStackTrace();
        }
    }

    private static String findProjectRoot() {
        Path current = Paths.get("").toAbsolutePath();
        while (current != null) {
            if (Files.exists(current.resolve("pom.xml")) ||
                    Files.exists(current.resolve("build.gradle")) ||
                    Files.exists(current.resolve("package.json"))) {
                return current.toString();
            }
            current = current.getParent();
        }
        return null;
    }

    private static ProjectStructure analyzeProject(File rootDir) throws IOException {
        ProjectStructure structure = new ProjectStructure();
        structure.projectName = rootDir.getName();
        structure.rootPath = rootDir.getAbsolutePath();
        structure.modules = new ArrayList<>();
        structure.dependencies = new ArrayList<>();
        structure.designPatterns = new HashMap<>();
        structure.securityFeatures = new HashMap<>();
        structure.configurations = new HashMap<>();
        structure.dbSchema = new HashMap<>();

        // Analyze project configuration files
        analyzeProjectConfiguration(rootDir, structure);

        // Find and analyze all modules
        findModules(rootDir).forEach(moduleDir -> {
            try {
                ModuleStructure module = analyzeModule(moduleDir);
                structure.modules.add(module);
            } catch (IOException e) {
                System.err.println("Error analyzing module: " + moduleDir + " - " + e.getMessage());
            }
        });

        return structure;
    }

    private static void analyzeProjectConfiguration(File rootDir, ProjectStructure structure) throws IOException {
        // Analyze Maven POM
        File pomFile = new File(rootDir, "pom.xml");
        if (pomFile.exists()) {
            analyzePomFile(pomFile, structure);
        }

        // Analyze Gradle build file
        File gradleFile = new File(rootDir, "build.gradle");
        if (gradleFile.exists()) {
            analyzeGradleFile(gradleFile, structure);
        }

        // Analyze application properties/yaml files
        analyzePropertyFiles(rootDir, structure);
    }

    private static List<File> findModules(File rootDir) {
        List<File> modules = new ArrayList<>();
        if (isModuleDirectory(rootDir)) {
            modules.add(rootDir);
        }

        File[] files = rootDir.listFiles();
        if (files != null) {
            for (File file : files) {
                if (file.isDirectory() && !IGNORE_DIRECTORIES.contains(file.getName())) {
                    modules.addAll(findModules(file));
                }
            }
        }
        return modules;
    }

    private static boolean isModuleDirectory(File dir) {
        return dir.isDirectory() && (
                new File(dir, "pom.xml").exists() ||
                        new File(dir, "build.gradle").exists() ||
                        new File(dir, "src").exists()
        );
    }

    private static ModuleStructure analyzeModule(File moduleDir) throws IOException {
        ModuleStructure module = new ModuleStructure();
        module.name = moduleDir.getName();
        module.path = moduleDir.getAbsolutePath();
        module.packages = new ArrayList<>();
        module.resources = new HashMap<>();
        module.dependencies = new ArrayList<>();
        module.apiEndpoints = new HashMap<>();

        // Analyze source files
        File srcDir = new File(moduleDir, "src/main/java");
        if (srcDir.exists()) {
            analyzeSourceDirectory(srcDir, module);
        }

        // Analyze resources
        File resourcesDir = new File(moduleDir, "src/main/resources");
        if (resourcesDir.exists()) {
            analyzeResourceDirectory(resourcesDir, module);
        }

        return module;
    }

    private static void analyzeSourceDirectory(File srcDir, ModuleStructure module) throws IOException {
        Map<String, PackageStructure> packageMap = new HashMap<>();

        Files.walk(srcDir.toPath())
                .filter(Files::isRegularFile)
                .filter(path -> path.toString().endsWith(".java"))
                .forEach(path -> {
                    try {
                        File file = path.toFile();
                        String packageName = calculatePackageName(srcDir, file);
                        PackageStructure pkg = packageMap.computeIfAbsent(packageName,
                                k -> new PackageStructure() {{
                                    name = k;
                                    classes = new ArrayList<>();
                                }});

                        ClassStructure cls = CodeAnalyzer.analyzeJavaFile(file);
                        pkg.classes.add(cls);

                        // Check for database entities
                        if (cls.annotations.stream().anyMatch(a -> a.contains("@Entity"))) {
                            String content = new String(Files.readAllBytes(path));
                            TableInfo tableInfo = DatabaseAnalyzer.extractTableInfo(content);
                            if (tableInfo.name != null) {
                                module.resources.put("db_table_" + tableInfo.name, path.toString());
                            }
                        }

                        // Check for API endpoints
                        analyzeApiEndpoints(cls, module);

                    } catch (IOException e) {
                        System.err.println("Error analyzing file: " + path + " - " + e.getMessage());
                    }
                });

        module.packages.addAll(packageMap.values());
    }

    private static String calculatePackageName(File srcDir, File file) {
        String relativePath = file.getParentFile().getAbsolutePath()
                .substring(srcDir.getAbsolutePath().length() + 1);
        return relativePath.replace(File.separatorChar, '.');
    }

    private static void analyzeApiEndpoints(ClassStructure cls, ModuleStructure module) {
        if (cls.annotations.stream().anyMatch(a -> a.contains("@RestController") ||
                a.contains("@Controller"))) {
            cls.methods.forEach(method -> {
                List<String> mappings = method.annotations.stream()
                        .filter(a -> a.contains("Mapping"))
                        .collect(Collectors.toList());

                if (!mappings.isEmpty()) {
                    ApiEndpointInfo endpoint = new ApiEndpointInfo();
                    endpoint.method = extractHttpMethod(mappings.get(0));
                    endpoint.path = extractPath(mappings.get(0));
                    endpoint.parameters = method.parameters;
                    endpoint.responseType = method.returnType;

                    module.apiEndpoints.put(endpoint.method + " " + endpoint.path, endpoint);
                }
            });
        }
    }

    private static String extractHttpMethod(String mapping) {
        if (mapping.contains("GetMapping")) return "GET";
        if (mapping.contains("PostMapping")) return "POST";
        if (mapping.contains("PutMapping")) return "PUT";
        if (mapping.contains("DeleteMapping")) return "DELETE";
        if (mapping.contains("PatchMapping")) return "PATCH";
        return "UNKNOWN";
    }

    private static String extractPath(String mapping) {
        int startIndex = mapping.indexOf("(");
        int endIndex = mapping.lastIndexOf(")");
        if (startIndex > 0 && endIndex > startIndex) {
            String content = mapping.substring(startIndex + 1, endIndex);
            if (content.contains("value = ")) {
                return content.substring(content.indexOf("\"") + 1, content.lastIndexOf("\""));
            }
            if (content.startsWith("\"")) {
                return content.substring(1, content.lastIndexOf("\""));
            }
        }
        return "/";
    }

    private static void analyzeResourceDirectory(File resourcesDir, ModuleStructure module) throws IOException {
        Files.walk(resourcesDir.toPath())
                .filter(Files::isRegularFile)
                .forEach(path -> {
                    String relativePath = resourcesDir.toPath().relativize(path).toString();
                    module.resources.put(relativePath, path.toString());
                });
    }

    private static void analyzePomFile(File pomFile, ProjectStructure structure) throws IOException {
        // Add basic Maven project analysis
        structure.dependencies.add("Maven");
        // TODO: Add detailed POM analysis
    }

    private static void analyzeGradleFile(File gradleFile, ProjectStructure structure) throws IOException {
        // Add basic Gradle project analysis
        structure.dependencies.add("Gradle");
        // TODO: Add detailed Gradle build file analysis
    }

    private static void analyzePropertyFiles(File rootDir, ProjectStructure structure) throws IOException {
        // Analyze application.properties/yaml files
        File[] propertyFiles = rootDir.listFiles((dir, name) ->
                name.startsWith("application.") &&
                        (name.endsWith(".properties") || name.endsWith(".yml") || name.endsWith(".yaml"))
        );

        if (propertyFiles != null) {
            for (File file : propertyFiles) {
                structure.configurations.put(file.getName(), file.getAbsolutePath());
            }
        }
    }
}

package org.sidequest.parley.scripts;

import java.io.File;
import java.io.PrintWriter;
import java.util.Scanner;

public class FixImportsSQLfile {
    public static void main(String[] args) {
        try {
            String projectRoot = System.getProperty("user.dir");
            File inputFile = new File(projectRoot + "\\parley-service-jpa\\src\\main\\resources\\import.sql");
            Scanner scanner = new Scanner(inputFile);
            PrintWriter writer = new PrintWriter(projectRoot + "\\parley-service-jpa\\src\\main\\resources\\reformatted_import.txt");

            StringBuilder sb = new StringBuilder();
            String previousLine = null;
            while (scanner.hasNextLine()) {
                String line = scanner.nextLine().trim();
                sb.append(line);
                // Handles comments and MySQL delimiter
                if (line.startsWith("#") || line.startsWith("--")) {

                    writer.println(sb.toString());
                    writer.println("\n");
                    // Clear the StringBuilder for the next SQL statement
                    sb.setLength(0);
                }

                // If the line ends with a semicolon, it's the end of a SQL statement
                if (line.endsWith(";")) {
                    writer.println(sb.toString());
                    // Clear the StringBuilder for the next SQL statement
                    sb.setLength(0);
                } else if (!line.isEmpty() || (line.isEmpty() && (previousLine == null || !previousLine.isEmpty()))) {
                    // If the line doesn't end with a semicolon, add a space before the next line
                    sb.append(" ");
                }
                previousLine = line;
            }

            scanner.close();
            writer.close();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

package org.sidequest.parley.scripts;

import java.security.SecureRandom;
import java.util.Base64;

public class Generate256bitKey {

    public static void main(String[] args) {
        SecureRandom secureRandom = new SecureRandom();
        byte[] key = new byte[32];
        secureRandom.nextBytes(key);
        String secret = Base64.getEncoder().encodeToString(key);
        System.out.println("The new secret key is: " + secret);
    }
}

package org.sidequest.parley.scripts.model;

import java.util.List;
import java.util.Map;

public class ProjectStructureModels {
    public static class ProjectStructure {
        public String projectName;
        public String rootPath;
        public List<ModuleStructure> modules;
        public List<String> dependencies;
        public Map<String, List<String>> designPatterns;
        public Map<String, List<String>> securityFeatures;
        public Map<String, String> configurations;
        public Map<String, TableInfo> dbSchema;
    }

    public static class ModuleStructure {
        public String name;
        public String path;
        public List<PackageStructure> packages;
        public Map<String, String> resources;
        public List<String> dependencies;
        public Map<String, ApiEndpointInfo> apiEndpoints;
    }

    public static class PackageStructure {
        public String name;
        public List<ClassStructure> classes;
    }

    public static class ClassStructure {
        public String name;
        public String path;
        public String type;
        public List<String> imports;
        public List<String> annotations;
        public String content;
        public List<MethodInfo> methods;
        public List<String> businessRules;
        public List<String> relatedClasses;
        public List<String> dependencies;
    }

    public static class MethodInfo {
        public String name;
        public String returnType;
        public List<String> parameters;
        public List<String> annotations;
        public String visibility;
        public boolean isAsync;
        public List<String> exceptions;
    }

    public static class ApiEndpointInfo {
        public String path;
        public String method;
        public String description;
        public List<String> parameters;
        public String requestBody;
        public String responseType;
    }

    public static class TableInfo {
        public String name;
        public List<ColumnInfo> columns;
        public List<String> relationships;
    }

    public static class ColumnInfo {
        public String name;
        public String type;
        public boolean isPrimaryKey;
        public boolean isForeignKey;
        public String referencedTable;
        public String referencedColumn;
        public boolean isNullable;
        public String defaultValue;
    }
}

package org.sidequest.parley.scripts.analyzers;

import org.apache.commons.io.FileUtils;
import org.sidequest.parley.scripts.model.ProjectStructureModels.ClassStructure;
import org.sidequest.parley.scripts.model.ProjectStructureModels.MethodInfo;

import java.io.File;
import java.io.IOException;
import java.nio.charset.StandardCharsets;
import java.util.*;
import java.util.regex.Matcher;
import java.util.regex.Pattern;
import java.util.stream.Collectors;

public class CodeAnalyzer {
    private static final Pattern IMPORT_PATTERN =
            Pattern.compile("import\\s+([\\w\\.\\*]+);");
    private static final Pattern ANNOTATION_PATTERN =
            Pattern.compile("@([\\w\\.]+)(?:\\([^)]*\\))?");
    private static final Pattern METHOD_PATTERN = Pattern.compile(
            "(?<visibility>public|private|protected)?\\s*" +
                    "(?<static>static\\s+)?" +
                    "(?<final>final\\s+)?" +
                    "(?<async>async\\s+)?" +
                    "(?<generic><[^>]+>\\s*)?" +
                    "(?<returnType>[\\w\\<\\>\\[\\]\\s\\.]+)\\s+" +
                    "(?<name>\\w+)\\s*" +
                    "\\((?<params>[^)]*)\\)\\s*" +
                    "(?<throws>throws\\s+[\\w\\s,\\.]+)?\\s*" +
                    "(?:\\{|;)", // Handle both method bodies and interface methods
            Pattern.MULTILINE
    );

    private static final Pattern METHOD_ANNOTATION_PATTERN = Pattern.compile(
            "@([\\w\\.]+)(?:\\([^)]*\\))?\\s*$",
            Pattern.MULTILINE
    );

    public static ClassStructure analyzeJavaFile(File file) throws IOException {
        if (file == null || !file.exists()) {
            throw new IllegalArgumentException("Invalid file provided for analysis");
        }

        ClassStructure cls = new ClassStructure();
        cls.name = file.getName().replace(".java", "");
        cls.path = file.getAbsolutePath();

        try {
            String content = FileUtils.readFileToString(file, StandardCharsets.UTF_8);
            cls.content = content;

            cls.imports = extractImports(content);
            cls.annotations = extractAnnotations(content);
            cls.type = determineClassType(content);
            cls.methods = extractMethods(content);
            cls.businessRules = extractBusinessRules(content);
            cls.relatedClasses = findRelatedClasses(content);
            cls.dependencies = findDependencies(content);

            return cls;
        } catch (IOException e) {
            throw new IOException("Failed to analyze Java file: " + file.getName(), e);
        }
    }

    public static List<String> extractImports(String content) {
        if (content == null || content.isEmpty()) {
            return new ArrayList<>();
        }

        List<String> imports = new ArrayList<>();
        Matcher matcher = IMPORT_PATTERN.matcher(content);
        while (matcher.find()) {
            imports.add(matcher.group(1));
        }
        return imports;
    }

    public static List<String> extractAnnotations(String content) {
        if (content == null || content.isEmpty()) {
            return new ArrayList<>();
        }

        List<String> annotations = new ArrayList<>();
        Matcher matcher = ANNOTATION_PATTERN.matcher(content);
        while (matcher.find()) {
            annotations.add(matcher.group());
        }
        return annotations;
    }

    public static List<MethodInfo> extractMethods(String content) {
        if (content == null || content.isEmpty()) {
            return new ArrayList<>();
        }

        List<MethodInfo> methods = new ArrayList<>();
        Matcher matcher = METHOD_PATTERN.matcher(content);

        while (matcher.find()) {
            try {
                MethodInfo method = new MethodInfo();
                method.name = matcher.group("name");

                String returnType = matcher.group("returnType");
                String generic = matcher.group("generic");
                method.returnType = (generic != null) ? generic.trim() + " " + returnType.trim() : returnType.trim();

                method.visibility = matcher.group("visibility") != null ?
                        matcher.group("visibility") : "package-private";
                method.isAsync = matcher.group("async") != null;
                method.parameters = parseParameters(matcher.group("params"));
                method.annotations = extractMethodAnnotations(
                        content.substring(Math.max(0, matcher.start() - 200), matcher.start())
                );
                method.exceptions = parseExceptions(matcher.group("throws"));
                methods.add(method);
            } catch (Exception e) {
                System.err.println("Error parsing method at position " + matcher.start() + ": " + e.getMessage());
            }
        }
        return methods;
    }

    private static List<String> extractMethodAnnotations(String precedingContent) {
        if (precedingContent == null || precedingContent.isEmpty()) {
            return new ArrayList<>();
        }

        List<String> annotations = new ArrayList<>();
        Matcher matcher = METHOD_ANNOTATION_PATTERN.matcher(precedingContent);
        while (matcher.find()) {
            annotations.add(matcher.group());
        }
        return annotations;
    }

    private static List<String> parseParameters(String params) {
        if (params == null || params.trim().isEmpty()) {
            return new ArrayList<>();
        }
        return Arrays.stream(params.split(","))
                .map(String::trim)
                .filter(s -> !s.isEmpty())
                .collect(Collectors.toList());
    }

    private static List<String> parseExceptions(String throwsClause) {
        if (throwsClause == null || throwsClause.trim().isEmpty()) {
            return new ArrayList<>();
        }
        return Arrays.stream(throwsClause.replace("throws", "").split(","))
                .map(String::trim)
                .filter(s -> !s.isEmpty())
                .collect(Collectors.toList());
    }

    public static List<String> extractBusinessRules(String content) {
        if (content == null || content.isEmpty()) {
            return new ArrayList<>();
        }

        List<String> rules = new ArrayList<>();
        Pattern pattern = Pattern.compile(
                "/\\*\\*\\s*(.*?)\\s*\\*/|//\\s*(.+)",
                Pattern.DOTALL | Pattern.MULTILINE
        );
        Matcher matcher = pattern.matcher(content);
        while (matcher.find()) {
            try {
                String comment = matcher.group(1) != null ? matcher.group(1) : matcher.group(2);
                if (comment != null && (
                        comment.toLowerCase().contains("rule") ||
                                comment.toLowerCase().contains("validation") ||
                                comment.toLowerCase().contains("requirement"))) {
                    rules.add(comment.trim());
                }
            } catch (Exception e) {
                System.err.println("Error parsing business rule comment: " + e.getMessage());
            }
        }
        return rules;
    }

    public static String determineClassType(String content) {
        if (content == null || content.isEmpty()) {
            return "unknown";
        }

        // Use more precise pattern matching to avoid false positives
        Pattern classPattern = Pattern.compile("\\s(class|interface|enum|@interface|record)\\s");
        Matcher matcher = classPattern.matcher(content);

        if (matcher.find()) {
            return matcher.group(1);
        }

        return "class"; // default to class if no specific type is found
    }

    private static List<String> findRelatedClasses(String content) {
        if (content == null || content.isEmpty()) {
            return new ArrayList<>();
        }

        Set<String> related = new HashSet<>();
        Pattern pattern = Pattern.compile(
                "(?:extends|implements|<)\\s+([\\w\\s,\\.]+)(?:>|\\{|\\s)"
        );
        Matcher matcher = pattern.matcher(content);
        while (matcher.find()) {
            Arrays.stream(matcher.group(1).split(","))
                    .map(String::trim)
                    .filter(s -> !s.isEmpty())
                    .forEach(related::add);
        }
        return new ArrayList<>(related);
    }

    private static List<String> findDependencies(String content) {
        if (content == null || content.isEmpty()) {
            return new ArrayList<>();
        }

        Set<String> dependencies = new HashSet<>();

        // Spring Framework dependencies
        if (content.contains("@Autowired") ||
                content.contains("@Component") ||
                content.contains("@Service") ||
                content.contains("@Repository")) {
            dependencies.add("Spring Framework");
        }

        // JPA dependencies
        if (content.contains("@Entity") ||
                content.contains("@Table") ||
                content.contains("@Column")) {
            dependencies.add("JPA");
        }

        // Security dependencies
        if (content.contains("@Secured") ||
                content.contains("@PreAuthorize") ||
                content.contains("@RolesAllowed")) {
            dependencies.add("Spring Security");
        }

        // Testing dependencies
        if (content.contains("@Test") ||
                content.contains("@Mock") ||
                content.contains("@MockBean")) {
            dependencies.add("Testing Framework");
        }

        return new ArrayList<>(dependencies);
    }
}

