package org.sidequest.parley.controller;

import org.sidequest.parley.api.ChatsApi;
import org.sidequest.parley.model.ChatMessage;
import org.sidequest.parley.model.NewChatMessage;
import org.sidequest.parley.service.ChatMessageService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.annotation.Lazy;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.RestController;

import java.util.logging.Logger;

@RestController
public class ChatMessageController implements ChatsApi {
    private static final Logger log = Logger.getLogger(ChatMessageController.class.getName());

    ChatMessageService chatMessageService;

    @Autowired
    public void ChatMessageService(@Lazy ChatMessageService chatMessageService) {
        this.chatMessageService = chatMessageService;
    }

    @Override
    public ResponseEntity<ChatMessage> getChatMessageById(Long id) {
        try {
            log.info("getChatMessageById: " + id);

            ChatMessage cm = chatMessageService.getChatMessage(id);
            return ResponseEntity.ok(cm);
        } catch (Exception e) {
            return ResponseEntity.notFound().build();
        }
    }

    @Override
    public ResponseEntity<ChatMessage> createChatMessage(NewChatMessage newChatMessage) {
        try {

            ChatMessage chatMessage = chatMessageService.createChatMessage(newChatMessage);
            if (chatMessage == null) {
                return ResponseEntity.notFound().build();
            }
            return new ResponseEntity<>(chatMessage, HttpStatus.CREATED);
        } catch (Exception e) {
            log.severe("Error creating chat message:" + e);
            return ResponseEntity.notFound().build();
        }
    }
}

package org.sidequest.parley.controller;


import org.sidequest.parley.api.ChatroomsApi;
import org.sidequest.parley.model.ChatMessage;
import org.sidequest.parley.model.ChatRoom;
import org.sidequest.parley.model.Error;
import org.sidequest.parley.model.NewChatRoom;
import org.sidequest.parley.service.ChatRoomService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.context.annotation.Lazy;
import org.springframework.core.io.InputStreamResource;
import org.springframework.core.io.Resource;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.RestController;
import org.springframework.web.multipart.MultipartFile;

import java.io.File;
import java.io.FileInputStream;
import java.util.List;
import java.util.logging.Logger;


/**
 * Controller class for managing chat rooms.
 */
@RestController
public class ChatRoomController implements ChatroomsApi {
    private static final Logger log = Logger.getLogger(ChatRoomController.class.getName());

    private ChatRoomService chatRoomService;

    @Autowired
    public void setChatRoomService(@Lazy ChatRoomService chatRoomService) {
        this.chatRoomService = chatRoomService;
    }

    @Override
    public ResponseEntity<List<ChatRoom>> getChatRooms() {

        try {

            List<ChatRoom> chatRooms = chatRoomService.getChatRooms();
            for (ChatRoom chatRoom : chatRooms) {
                log.info("**** chatRoom: " + chatRoom.getName());
            }
            return ResponseEntity.ok(chatRooms);
        } catch (Exception e) {
            return ResponseEntity.notFound().build();
        }
    }

    @Override
    public ResponseEntity<ChatRoom> getChatRoomById(Long id) {
        try {
            ChatRoom chatRoom = chatRoomService.getChatRoom(id);
            return ResponseEntity.ok(chatRoom);
        } catch (Exception e) {
            return ResponseEntity.notFound().build();
        }
    }

    @Override
    public ResponseEntity<ChatRoom> createChatRoom(NewChatRoom newChatRoom) {
        try {
            ChatRoom chatRoom = chatRoomService.createChatRoom(newChatRoom);
            if (chatRoom != null) {
                return new ResponseEntity<>(chatRoom, HttpStatus.CREATED);
            } else {
                return ResponseEntity.notFound().build();
            }
        } catch (Exception e) {
            log.severe("Error creating chat room: " + e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).build();
        }
    }

    @Override
    public ResponseEntity<ChatRoom> updateChatRoom(Long id, ChatRoom chatRoom) {
        try {
            ChatRoom resultChatRoom = chatRoomService.updateChatRoom(id, chatRoom);
            if (resultChatRoom != null) {
                return new ResponseEntity<>(resultChatRoom, HttpStatus.CREATED);
            } else {
                return ResponseEntity.notFound().build();
            }
        } catch (Exception e) {
            log.severe("Error creating chat room: " + e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).build();
        }
    }

    @Override
    public ResponseEntity<Error> deleteChatRoom(Long id) {
        try {
            chatRoomService.deleteChatRoom(id);
            return ResponseEntity.ok().build();
        } catch (Exception e) {
            return ResponseEntity.notFound().build();
        }
    }

    @Override
    public ResponseEntity<Void> setChatRoomIcon(Long id, MultipartFile file) {
        try {
            chatRoomService.setChatRoomIcon(id, file);
            return ResponseEntity.ok().build();
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    @Override
    public ResponseEntity<Resource> getChatRoomIcon(Long id) {
        try {
            String avatarPath = chatRoomService.getChatRoomIcon(id);
            File file = new File(avatarPath);
            HttpHeaders headers = new HttpHeaders();
            headers.add(HttpHeaders.CONTENT_DISPOSITION, "inline;filename=" + file.getName());
            InputStreamResource resource = new InputStreamResource(new FileInputStream(file));
            return ResponseEntity.ok()
                    .headers(headers)
                    .contentLength(file.length())
                    .contentType(MediaType.IMAGE_PNG) // or MediaType.IMAGE_PNG if it's a PNG image MediaType.IMAGE_JPEG
                    .body(resource);
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    @Override
    public ResponseEntity<List<ChatMessage>> getChatroomChatsByChatRoomId(Long chatRoomId) {
        try {
            List<ChatMessage> chatMessages = chatRoomService.getChatroomChatsByChatRoomId(chatRoomId);
            return ResponseEntity.ok(chatMessages);
        } catch (Exception e) {
            return ResponseEntity.notFound().build();
        }
    }

}

package org.sidequest.parley.controller;

import org.sidequest.parley.api.EnrollmentsApi;
import org.sidequest.parley.model.Enrollment;
import org.sidequest.parley.model.NewEnrollment;
import org.sidequest.parley.service.EnrollmentService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.RestController;

import java.util.List;
import java.util.logging.Logger;

@RestController
public class EnrollmentController implements EnrollmentsApi {

    private static final Logger log = Logger.getLogger(EnrollmentController.class.getName());

    @Autowired
    private EnrollmentService enrollmentService;

    @Override
    public ResponseEntity<List<Enrollment>> addUsersToChatRooms(List<NewEnrollment> enrollment) {
        try {
            List<Enrollment> enrollments = enrollmentService.addUsersToChatRoom(enrollment);
            return ResponseEntity.status(HttpStatus.CREATED).body(enrollments);
        } catch (Exception e) {
            log.severe("Error adding users to chat rooms: " + e.getMessage());
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).build();
        }
    }

    @Override
    public ResponseEntity<Void> addUserToChatRoom(Long chatRoomId, Long userId) {
        try {
            Enrollment newEnrollment = enrollmentService.addUserToChatRoom(chatRoomId, userId);
            return ResponseEntity.status(HttpStatus.CREATED).build();
        } catch (Exception e) {
            log.severe("Error adding user to chat room: " + e.getMessage());
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).build();
        }
    }

    @Override
    public ResponseEntity<List<Enrollment>> getEnrollmentsByChatRoomId(Long chatRoomId) {
        try {
            List<Enrollment> enrollments = enrollmentService.getEnrollmentsByChatRoomId(chatRoomId);
            return ResponseEntity.status(HttpStatus.CREATED).body(enrollments);
        } catch (Exception e) {
            log.severe("Error getting enrollments by chat room id: " + e.getMessage());
            return ResponseEntity.notFound().build();
        }
    }


    @Override
    public ResponseEntity<Void> removeUserFromChatRoom(Long chatRoomId, Long userId) {
        try {
            enrollmentService.removeUserFromChatRoom(chatRoomId, userId);
            return ResponseEntity.ok().build();
        } catch (Exception e) {
            log.severe("Error removing user from chat room: " + e.getMessage());
            return ResponseEntity.notFound().build();
        }
    }

    @Override
    public ResponseEntity<Void> removeUsersFromChatRoom(Long chatRoomId) {
        try {
            enrollmentService.removeUsersFromChatRoom(chatRoomId);
            return ResponseEntity.ok().build();
        } catch (Exception e) {
            log.severe("Error removing users from chat room: " + e.getMessage());
            return ResponseEntity.notFound().build();
        }
    }
}

