package org.sidequest.parley.controller;

import org.sidequest.parley.api.UsersApi;
import org.sidequest.parley.model.ChatRoom;
import org.sidequest.parley.model.NewUser;
import org.sidequest.parley.model.User;
import org.sidequest.parley.service.UserService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.core.io.InputStreamResource;
import org.springframework.core.io.Resource;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.RestController;
import org.springframework.web.multipart.MultipartFile;

import java.io.File;
import java.io.FileInputStream;
import java.util.List;
import java.util.logging.Logger;

/**
 * A RESTful web service controller for managing users.
 */
//@Service
@RestController
public class UserController implements UsersApi {
    private static final Logger log = Logger.getLogger(UserController.class.getName());

    @Autowired
    UserService userService;

    /**
     * Retrieves a list of all users in JSON format.
     *
     * @return A JSON-formatted string containing an array of users.
     */
    @Override
//    public ResponseEntity<List<org.sidequest.parley.model.User>> getUsers() {
    public ResponseEntity<List<User>> getUsers() {
        try {
            List<User> users = userService.getUsers();
            return ResponseEntity.ok(users);
        } catch (Exception e) {
            return ResponseEntity.notFound().build();
        }
    }

    @Override
    public ResponseEntity<User> getUserById(Long id) {
        try {
            User user = userService.getUser(id);
            return ResponseEntity.ok(user);
        } catch (Exception e) {
            return ResponseEntity.notFound().build();
        }
    }

    @Override
    public ResponseEntity<User> createUser(NewUser newUser) {
        try {
            log.fine("createUser: " + newUser.getName() + " " + newUser.getTimezone());
            User user = userService.createUser(newUser.getName(), newUser.getTimezone());
            if (user == null) {
                return ResponseEntity.notFound().build();
            }
            return new ResponseEntity<>(user, HttpStatus.CREATED);
        } catch (IllegalArgumentException e) {
            log.severe("Error creating user:" + e);
            return ResponseEntity.badRequest().build();
        } catch (Exception e) {
            log.severe("Error creating user:" + e);
            return ResponseEntity.notFound().build();
        }
    }

    @Override
    public ResponseEntity<Void> setUserAvatar(Long id, MultipartFile file) {
        try {
            userService.setUserAvatar(id, file);
            return ResponseEntity.ok().build();
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    @Override
    public ResponseEntity<Resource> getUserAvatar(Long id) {
        try {
            String avatarPath = userService.getUserAvatar(id);
            File file = new File(avatarPath);
            HttpHeaders headers = new HttpHeaders();
            headers.add(HttpHeaders.CONTENT_DISPOSITION, "inline;filename=" + file.getName());
            InputStreamResource resource = new InputStreamResource(new FileInputStream(file));
            return ResponseEntity.ok()
                    .headers(headers)
                    .contentLength(file.length())
                    .contentType(MediaType.IMAGE_PNG) // or MediaType.IMAGE_PNG if it's a PNG image MediaType.IMAGE_JPEG
                    .body(resource);
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }

    @Override
    public ResponseEntity<List<ChatRoom>> getChatRoomsByUserId(Long userId) {
        try {
            List<ChatRoom> chatRooms = userService.getChatRoomsByUserId(userId);
            return ResponseEntity.ok(chatRooms);
        } catch (Exception e) {
            log.severe("Error getting chat rooms for user: " + e.getMessage());
            return ResponseEntity.notFound().build();
        }
    }

    @Override
    public ResponseEntity<User> updateUserById(Long id, NewUser newUser) {
        try {
            User user = userService.updateUserById(id, newUser);
            //return ResponseEntity.ok().build();
            return new ResponseEntity<>(user, HttpStatus.CREATED);
        } catch (Exception e) {
            return ResponseEntity.badRequest().build();
        }
    }
}

package org.sidequest.parley.util;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

import javax.mail.*;
import javax.mail.internet.InternetAddress;
import javax.mail.internet.MimeMessage;
import java.util.Properties;

@Component
public class EmailHelper {

    @Value("${spring.mail.host}")
    private String smtpHost;

    @Value("${spring.mail.port}")
    private int smtpPort;

    @Value("${spring.mail.username}")
    private String smtpUsername;

    @Value("${spring.mail.password}")
    private String smtpPassword;

    @Value("${spring.mail.from}")
    private String fromEmail;

    public void sendPasswordResetEmail(String email, String resetToken) {
        Properties props = new Properties();
        props.put("mail.smtp.auth", "true");
        props.put("mail.smtp.starttls.enable", "true");
        props.put("mail.smtp.host", smtpHost);
        props.put("mail.smtp.port", String.valueOf(smtpPort));

        Session session = Session.getInstance(props, new Authenticator() {
            protected PasswordAuthentication getPasswordAuthentication() {
                return new PasswordAuthentication(smtpUsername, smtpPassword);
            }
        });

        try {
            Message message = new MimeMessage(session);
            message.setFrom(new InternetAddress(fromEmail));
            message.setRecipients(Message.RecipientType.TO, InternetAddress.parse(email));
            message.setSubject("Password Reset Request");

            String resetLink = "http://localhost:8080/parley/reset-password?token=" + resetToken;
            StringBuilder emailContent = new StringBuilder();
            emailContent.append("Hello,\n\n");
            emailContent.append("A password reset was requested for your Parley account.\n\n");
            emailContent.append("Click the following link to reset your password:\n");
            emailContent.append(resetLink).append("\n\n");
            emailContent.append("This link will expire in 24 hours.\n\n");
            emailContent.append("If you did not request this password reset, please ignore this email.\n\n");
            emailContent.append("Best regards,\nThe Parley Team");

            message.setText(emailContent.toString());

            Transport.send(message);
        } catch (MessagingException e) {
            throw new RuntimeException("Failed to send password reset email", e);
        }
    }
}

package org.sidequest.parley.util;

import org.springframework.stereotype.Component;
import org.springframework.web.multipart.MultipartFile;

import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.ArrayList;
import java.util.List;
import java.util.logging.Level;
import java.util.logging.Logger;

@Component
public class FileSystemHelper {

    private static final Logger logger = Logger.getLogger(FileSystemHelper.class.getName());

    // Save a file to a specified directory and return the path of the saved file
    public String saveFile(MultipartFile file, String directoryPath) {
        if (file == null || directoryPath == null) {
            logger.warning("File or directoryPath is null.");
            throw new IllegalArgumentException("File and directoryPath cannot be null.");
        }

        try {
            File directory = new File(directoryPath);
            if (!directory.exists()) {
                boolean dirsCreated = directory.mkdirs();
                if (!dirsCreated) {
                    logger.warning("Failed to create directories: " + directoryPath);
                    throw new IOException("Failed to create directories: " + directoryPath);
                }
            }

            File destinationFile = new File(directory, file.getOriginalFilename());
            file.transferTo(destinationFile);
            logger.info("File saved successfully: " + destinationFile.getAbsolutePath());
            return destinationFile.getAbsolutePath();
        } catch (IOException e) {
            logger.log(Level.SEVERE, "Failed to save file: " + file.getOriginalFilename(), e);
            throw new RuntimeException("Failed to save file: " + file.getOriginalFilename(), e);
        }
    }

    // Delete a file
    public boolean deleteFile(String filePath) {
        if (filePath == null) {
            logger.warning("filePath is null.");
            throw new IllegalArgumentException("filePath cannot be null.");
        }

        try {
            File file = new File(filePath);
            if (file.exists() && file.delete()) {
                logger.info("File deleted successfully: " + filePath);
                return true;
            } else {
                logger.warning("File not found or could not be deleted: " + filePath);
                return false;
            }
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Failed to delete file: " + filePath, e);
            throw new RuntimeException("Failed to delete file: " + filePath, e);
        }
    }

    // List all files in a directory
    public List<String> listFiles(String directoryPath) {
        if (directoryPath == null) {
            logger.warning("directoryPath is null.");
            throw new IllegalArgumentException("directoryPath cannot be null.");
        }

        List<String> fileList = new ArrayList<>();
        try {
            File directory = new File(directoryPath);
            if (directory.exists() && directory.isDirectory()) {
                File[] files = directory.listFiles();
                if (files != null) {
                    for (File file : files) {
                        fileList.add(file.getAbsolutePath());
                    }
                }
                logger.info("Listed files in directory: " + directoryPath);
            } else {
                logger.warning("Directory not found or is not a directory: " + directoryPath);
            }
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Failed to list files in directory: " + directoryPath, e);
            throw new RuntimeException("Failed to list files in directory: " + directoryPath, e);
        }
        return fileList;
    }

    // Check if a file or directory exists
    public boolean exists(String path) {
        if (path == null) {
            logger.warning("path is null.");
            throw new IllegalArgumentException("path cannot be null.");
        }

        try {
            File file = new File(path);
            boolean exists = file.exists();
            logger.info("File or directory " + (exists ? "exists" : "does not exist") + ": " + path);
            return exists;
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Failed to check existence of path: " + path, e);
            throw new RuntimeException("Failed to check existence of path: " + path, e);
        }
    }

    // Create a directory
    public boolean createDirectory(String directoryPath) {
        if (directoryPath == null) {
            logger.warning("directoryPath is null.");
            throw new IllegalArgumentException("directoryPath cannot be null.");
        }

        try {
            File directory = new File(directoryPath);
            if (!directory.exists()) {
                boolean created = directory.mkdirs();
                logger.info("Directory " + (created ? "created" : "not created") + ": " + directoryPath);
                return created;
            } else {
                logger.warning("Directory already exists: " + directoryPath);
                return false;
            }
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Failed to create directory: " + directoryPath, e);
            throw new RuntimeException("Failed to create directory: " + directoryPath, e);
        }
    }

    // Read the content of a file as a string
    public String readFileContent(String filePath) {
        if (filePath == null) {
            logger.warning("filePath is null.");
            throw new IllegalArgumentException("filePath cannot be null.");
        }

        try {
            Path path = Paths.get(filePath);
            String content = Files.readString(path);
            logger.info("Read file content: " + filePath);
            return content;
        } catch (IOException e) {
            logger.log(Level.SEVERE, "Failed to read file content: " + filePath, e);
            throw new RuntimeException("Failed to read file content: " + filePath, e);
        }
    }

    // Write content to a file
    public void writeFileContent(String filePath, String content) {
        if (filePath == null || content == null) {
            logger.warning("filePath or content is null.");
            throw new IllegalArgumentException("filePath and content cannot be null.");
        }

        try {
            Path path = Paths.get(filePath);
            Files.writeString(path, content);
            logger.info("Wrote content to file: " + filePath);
        } catch (IOException e) {
            logger.log(Level.SEVERE, "Failed to write file content: " + filePath, e);
            throw new RuntimeException("Failed to write file content: " + filePath, e);
        }
    }
}

package org.sidequest.parley.util;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

import java.time.OffsetDateTime;
import java.time.ZoneId;
import java.time.ZoneOffset;
import java.time.zone.ZoneRulesException;
import java.util.logging.Logger;

/**
 * A utility class that provides helper methods for managing time-related operations.
 */
@Component
public class TimeHelper {
    private static final Logger log = Logger.getLogger(TimeHelper.class.getName());

    @Value("${default.timezone}")
    private String defaultTimezone;

    public ZoneId getZoneId(String timezone) {
        try {
            return timezone != null ? ZoneId.of(timezone) : null;
        } catch (Exception e) {
            log.warning("Invalid timezone: " + timezone + ". Using default: " + defaultTimezone);
            return null;
        }
    }

    public ZoneId getDefaultZoneId() {
        return ZoneId.of(defaultTimezone);
    }

    public boolean isTimezone(String timezone) {
        try {
            ZoneId.of(timezone);
            return true;
        } catch (ZoneRulesException e) {
            return false;
        }
    }


    /**
     * Converts a ZonedDateTime to UTC.
     *
     * @param localDateTime the OffsetDateTime to be converted to UTC
     * @return the OffsetDateTime in UTC
     */
    public static OffsetDateTime toUtc(OffsetDateTime localDateTime) {
        try {
            if (localDateTime == null) {
                return null;
            }
            return localDateTime.withOffsetSameInstant(ZoneOffset.UTC);
        } catch (Exception e) {
            log.severe("Error converting to UTC: " + e);
            return null;
        }
    }

    /**
     * Converts an OffsetDateTime to the local time of the specified ZoneId.
     *
     * @param odt     the OffsetDateTime to be converted
     * @param zoneId the ZoneId to convert to
     * @return the OffsetDateTime in the local time of the specified ZoneId
     */
    public static OffsetDateTime fromUtc(OffsetDateTime odt, ZoneId zoneId) {
        try {
            if (odt == null || zoneId == null) {
                return null;
            }
            return odt.atZoneSameInstant(zoneId).toOffsetDateTime();
        } catch (Exception e) {
            log.severe("Error converting from UTC: " + e);
            return null;
        }
    }

}

